(3, 'editdb', '', '$lastname = $_POST[\'lastname\'];\r\n$firstname = $_POST[\'firstname\'];\r\n$option = $_POST[\'option\'];\r\n$input = $_POST[\'input\'];\r\n\r\n$link = mysql_connect(\'localhost\', \'root\', \'pi\');\r\nif (!$link) {\r\n    die(\'Could not connect: \' . mysql_error());\r\n}\r\nif (!mysql_select_db(\'srts\')) {\r\n    die(\'Could not select database: \' . mysql_error());\r\n}\r\n$result = mysql_query(\'SELECT RFID FROM tag\');\r\nif (!$result) {\r\n    die(\'Could not query:\' . mysql_error());\r\n}\r\n\r\n$value = mysql_result($result, 0);\r\n//echo mysql_result($result, 0); // outputs third employee\'s name\r\n\r\nmysql_close($link);', '', 0, 0),
(4, 'downoadexcel', '', '/*******EDIT LINES 3-8*******/\r\n$DB_Server = \"localhost\"; //MySQL Server    \r\n$DB_Username = \"root\"; //MySQL Username     \r\n$DB_Password = \"pi\";             //MySQL Password     \r\n$DB_DBName = \"srts\";         //MySQL Database Name  \r\n$DB_TBLName = \"students\"; //MySQL Table Name   \r\n$filename = \"SRTS Database Capture\";         //File Name\r\n/*******YOU DO NOT NEED TO EDIT ANYTHING BELOW THIS LINE*******/    \r\n//create MySQL connection   \r\n$sql = \"Select LastName, FirstName,Count(AtRFID),Class,grade,RFID From students LEFT JOIN attendance ON attendance.AtRFID=students.RFID GROUP BY id ORDER BY LastName\";\r\n$Connect = @mysql_connect($DB_Server, $DB_Username, $DB_Password) or die(\"Couldn\'t connect to MySQL:<br>\" . mysql_error() . \"<br>\" . mysql_errno());\r\n//select database   \r\n$Db = @mysql_select_db($DB_DBName, $Connect) or die(\"Couldn\'t select database:<br>\" . mysql_error(). \"<br>\" . mysql_errno());   \r\n//execute query \r\n$result = @mysql_query($sql,$Connect) or die(\"Couldn\'t execute query:<br>\" . mysql_error(). \"<br>\" . mysql_errno());    \r\n$file_ending = \"xls\";\r\n//header info for browser\r\nheader(\"Content-Type: application/xls\");    \r\nheader(\"Content-Disposition: attachment; filename=$filename.xls\");  \r\nheader(\"Pragma: no-cache\"); \r\nheader(\"Expires: 0\");\r\n/*******Start of Formatting for Excel*******/   \r\n//define separator (defines columns in excel & tabs in word)\r\n$sep = \"\\t\"; //tabbed character\r\n//start of printing column names as names of MySQL fields\r\nfor ($i = 0; $i < mysql_num_fields($result); $i++) {\r\necho mysql_field_name($result,$i) . \"\\t\";\r\n}\r\nprint(\"\\n\");    \r\n//end of printing column names  \r\n//start while loop to get data\r\n    while($row = mysql_fetch_row($result))\r\n    {\r\n        $schema_insert = \"\";\r\n        for($j=0; $j<mysql_num_fields($result);$j++)\r\n        {\r\n            if(!isset($row[$j]))\r\n                $schema_insert .= \"NULL\".$sep;\r\n            elseif ($row[$j] != \"\")\r\n                $schema_insert .= \"$row[$j]\".$sep;\r\n            else\r\n                $schema_insert .= \"\".$sep;\r\n        }\r\n        $schema_insert = str_replace($sep.\"$\", \"\", $schema_insert);\r\n        $schema_insert = preg_replace(\"/\\r\\n|\\n\\r|\\n|\\r/\", \" \", $schema_insert);\r\n        $schema_insert .= \"\\t\";\r\n        print(trim($schema_insert));\r\n        print \"\\n\";\r\n    }   ', '', 0, 1),
(2, 'viewwholedb', '', '$host=\"localhost\"; // Host name \r\n$username=\"root\"; // Mysql username \r\n$password=\"pi\"; // Mysql password \r\n$db_name=\"srts\"; // Database name \r\n$tbl_name=\"students\"; // Table name \r\n\r\nmysql_connect($host, $username, $password)or die(\"cannot connect\");\r\nmysql_select_db($db_name)or die(\"cannot select DB\");\r\n$sql=\"Select Class, grade,RFID,LastName, FirstName,id, Count(AtRFID),already From students LEFT JOIN attendance ON attendance.AtRFID=students.RFID GROUP BY id ORDER BY LastName;\";\r\n$result=mysql_query($sql);\r\n$count=mysql_num_rows($result);', '', 0, 0);
