(1, 'View-Database', '$servername = \"localhost\";\r\n$username = \"srts_migrated\";\r\n$password = \"V.u!p?MU8gKR\";\r\n$dbname = \"srts_migrated\";\r\n\r\n// Create connection\r\n$conn = new mysqli($servername, $username, $password, $dbname);\r\n// Check connection\r\nif ($conn->connect_error) {\r\n     die(\"Connection failed: \" . $conn->connect_error);\r\n} \r\n\r\n$sql = \"SELECT * FROM users\";\r\n$result = $link->query($sql);\r\n\r\nif ($result->num_rows > 0) {\r\n     echo \"<table><tr><th>Current Students</th></tr>\";\r\n     // output data of each row\r\n     echo \"<tr><td>Username</td><td>First Name</td><td>Last Name</td><td>Grade</td></tr>\";\r\n     while($row = $result->fetch_assoc()) {\r\n         echo \"<tr><td>\" . $row[\"username\"]. \"</td><td>\" . $row[\"FirstName\"]. \"</td><td>\" . $row[\"LastName\"]. \"</td><td>\" . $row[\"grade\"]. \"</td></tr>\";\r\n     }\r\n     echo \"</table>\";\r\n} else {\r\n     echo \"0 results\";\r\n}\r\n\r\n$link->close();', '[xyz-ips snippet=\"View-Database\"]', 1),
(2, 'EDIT-RFID-Number', '<?php\r\n	//set up link at top for easy editing\r\n	$link = mysql_connect(\'localhost\', \'root\', \'pi\', \'srts\');\r\n\r\n	//get info about what error occured\r\n	$error = (int) $_GET[\'error\']; //0 if no error\r\n	\r\n	$errorMessage = \"\"; //default message\r\n	if ($error == 1) {\r\n		$errorMessage = \"No students match the given information.<br>\";\r\n	}\r\n	else if ($error == 2) {\r\n		$errorMessage = \"More than one student matches the given information.<br>\";\r\n	}\r\n	else if ($error == 3) {\r\n		$errorMessage = \"The tag may have not been scanned or the tag scanned is already in use.<br>\";\r\n	}\r\n	\r\n	//figure out which step the user is on based on the get info\r\n	$step = 1; //default step\r\n	if ($error == 3) { //tag was not valid\r\n		$step = 2;\r\n	}\r\n	else if ($error == 1 or $error == 2) { //student info was not valid\r\n		$step = 1;\r\n	}\r\n	else if (isset($_GET[\'scanned\'])) { //user has scanned tag and no errors\r\n		$step = 3;\r\n	}\r\n	else if (isset($_GET[\'user\']) and isset($_GET[\'email\'])) { //user and email is set along with no errors\r\n		$setp = 2;\r\n	}\r\n\r\n//part 3: user gets confirmation message\r\nif ($step == 3){\r\n	//get input from the last submit\r\n	$user = $_GET[\'user\'];\r\n	$email = $_GET[\'email\'];\r\n	\r\n	//connect to the database\r\n	if (!$link) {\r\n		die(\'Could not connect: \' . mysql_error());\r\n	}\r\n	if (!mysql_select_db(\'srts\')) {\r\n		die(\'Could not select database: \' . mysql_error());\r\n	}\r\n	\r\n	//get scanned tag\r\n	$query = \"SELECT `RFID` FROM `tag` ORDER BY `RFID` DESC LIMIT 1\";\r\n	$result = mysql_query($query);\r\n	if (!$result) {\r\n		die(\'Could not query:\' . mysql_error());\r\n	}\r\n	\r\n	$row = mysqli_fetch_assoc($result);\r\n	$scannedTag = $row[\'RFID\'];\r\n	\r\n	//check if the tag is valid\r\n	$query = \"SELECT `rfidtag` FROM `wpgf_users`\";\r\n	$result = mysql_query($query);\r\n	if (!$result) {\r\n		die(\'Could not query:\' . mysql_error());\r\n	}\r\n	\r\n	while ($row = mysqli_fetch_assoc($result)){\r\n		if ($row[\'rfidtag\'] == $scannedTag) {\r\n			//close database connection\r\n			mysqli_close($link);\r\n			\r\n			$error = 3;\r\n			header(\"Location: \" . htmlspecialchars($_SERVER[\"PHP_SELF\"]) . \"?user=$user&email=$email&error=$error\" );\r\n		}\r\n	}\r\n	\r\n	//get student from database\r\n	$query = \"SELECT `ID` FROM `wpgf_users` WHERE `user_login` = $user AND `user_email` = $email\";\r\n	$result = mysql_query($query);\r\n	if (!$result) {\r\n		die(\'Could not query:\' . mysql_error());\r\n	}\r\n	\r\n	//get unique id of the user\r\n	$row = mysqli_fetch_assoc($result);\r\n	$user_id = $row[\'ID\'];\r\n	\r\n	//change student\'s tag id\r\n	$query = \"UPDATE `wpgf_users` SET `rfidtag` = $scannedTag WHERE `ID` = $user_id\";\r\n	$result = mysql_query($query);\r\n	if (!$result) {\r\n		die(\'Could not query:\' . mysql_error());\r\n	}\r\n	\r\n	?>\r\n		<h4>\r\n			You have succesfully replaced the student\'s RFID tag!\r\n		</h4>\r\n		\r\n		<br>\r\n				\r\n		<!-- form to go to step 1 -->\r\n		<form action=\"<?php echo htmlspecialchars($_SERVER[\"PHP_SELF\"]);?>\">\r\n    		<input type=\"submit\" value=\"Change Another Tag\">\r\n		</form>\r\n	<?php\r\n}\r\n\r\n//part 2: user scans tag\r\nelse if ($step == 2) {\r\n	//get input from the last submit\r\n	$user = $_GET[\'user\'];\r\n	$email = $_GET[\'email\'];\r\n	\r\n	//connect to the database\r\n	if (!$link) {\r\n		die(\'Could not connect: \' . mysql_error());\r\n	}\r\n	if (!mysql_select_db(\'srts\')) {\r\n		die(\'Could not select database: \' . mysql_error());\r\n	}\r\n	\r\n	//get student from database\r\n	$query = \"SELECT `ID` FROM `wpgf_users` WHERE `user_login` = $user AND `user_email` = $email\";\r\n	$result = mysql_query($query);\r\n	if (!$result) {\r\n		die(\'Could not query:\' . mysql_error());\r\n	}\r\n	\r\n	//0 matches, go back to first step\r\n	if (mysqli_num_rows($result) <= 0) {\r\n		//close database connection\r\n		mysqli_close($link);\r\n		\r\n		$error = 1;\r\n		header(\"Location: \" . htmlspecialchars($_SERVER[\"PHP_SELF\"]) . \"?user=$user&email=$email&error=$error\" );\r\n	}\r\n	else if (mysqli_num_rows($result) > 1) { //too many matches, go back to first step\r\n		//close database connection\r\n		mysqli_close($link);\r\n		\r\n		$error = 2;\r\n		header(\"Location: \" . htmlspecialchars($_SERVER[\"PHP_SELF\"]) . \"?user=$user&email=$email&error=$error\" );\r\n	}\r\n	else { //found 1 match, continue with step 2\r\n		?>\r\n			<h2 style=\"color: red\"><?php echo $errorMessage;?></h2>\r\n		\r\n			<h3>\r\n				Please scan the new tag of the desired student. Then click \'Change Tag ID\'.\r\n			</h3>\r\n			\r\n			<br>\r\n			\r\n			<!-- form to submit student info -->\r\n			<form action=\"<?php echo htmlspecialchars($_SERVER[\"PHP_SELF\"]);?>\" method=\"get\">\r\n				<input type=\"hidden\" name=\"user\" value=\"<?php echo htmlspecialchars($user);?>\">\r\n				<input type=\"hidden\" name=\"email\" value=\"<?php echo htmlspecialchars($email);?>\">\r\n				<input type=\"hidden\" name=\"scanned\" value=\"true\">\r\n				<input type=\"submit\" name=\"submit\" value=\"Change Tag ID\">\r\n			</form>\r\n		<?php\r\n	}\r\n		\r\n}\r\n\r\n//part 1: user inputs student info\r\nelse {\r\n	//get input from the last submit\r\n	$user = $_GET[\'user\'];\r\n	$email = $_GET[\'email\'];\r\n	\r\n?>\r\n	<h2 style=\"color: red\"><?php echo $errorMessage;?></h2>\r\n\r\n	<h3>\r\n		Please input the information of the student who\'s tag should be replaced.\r\n	</h3>\r\n	\r\n	<br>\r\n			\r\n	<!-- form to submit student info -->\r\n	<form action=\"<?php echo htmlspecialchars($_SERVER[\"PHP_SELF\"]);?>\" method=\"get\">\r\n		Student Username: 	<input type=\"text\" name=\"user\" placeholder=\"username\" value=\"<?php echo htmlspecialchars($user);?>\"> <br>\r\n		Student Email:		<input type=\"text\" name=\"email\" placeholder=\"email\" value=\"<?php echo htmlspecialchars($email);?>\"> <br>\r\n		<input type=\"submit\" name=\"submit\" value=\"Next\">\r\n	</form>\r\n			\r\n<?php \r\n}\r\n\r\n//close database connection\r\nmysqli_close($link);\r\n?>', '[xyz-ips snippet=\"EDIT-RFID-Number\"]', 2),
(3, 'View-Students-Order-Class', '$servername = \"localhost\";\r\n$username = \"root\";\r\n$password = \"pi\";\r\n$dbname = \"srts\";\r\n\r\n// Create connection\r\n$conn = new mysqli($servername, $username, $password, $dbname);\r\n// Check connection\r\nif ($conn->connect_error) {\r\n     die(\"Connection failed: \" . $conn->connect_error);\r\n} \r\n\r\n$sql = \"SELECT DISTINCT(Class) AS Class FROM students ORDER BY Class ASC\";\r\n$result = $conn->query($sql);\r\n\r\nif ($result->num_rows > 0) {\r\n     echo \"<table><tr><th>Current Classes</th></tr>\";\r\n     // output data of each row\r\n     while($row = $result->fetch_assoc()) {\r\n         echo \"<tr><td>\" . $row[\"Class\"]. \"</td></tr>\";\r\n     }\r\n     echo \"</table>\";\r\n} else {\r\n     echo \"0 results\";\r\n}\r\n\r\n$conn->close();', '[xyz-ips snippet=\"View-Students-Order-Class\"]', 2),
