INSERT INTO `wpgf_pp_login_builder` VALUES (1,'FlatUI Login Theme','<div class=\"login-form\">\r\n\r\n<div class=\"form-group\">\r\n[login-username placeholder=\"username\" class=\"form-control login-field\" id=\"login-name\"]\r\n<label class=\"login-field-icon fui-user\" for=\"login-name\"></label>\r\n</div>\r\n\r\n<div class=\"form-group\">\r\n[login-password placeholder=\"password\" class=\"form-control login-field\" id=\"login-pass\"]\r\n<label class=\"login-field-icon fui-lock\" for=\"login-pass\"></label>\r\n</div>\r\n\r\n<div class=\"form-group\">\r\n[login-remember class=\"flat-checkbox\" id=\"remember-me\"]\r\n<label for=\"remember-me\" class=\"css-label lite-cyan-check\">Remember me</label>\r\n</div>\r\n\r\n[login-submit value=\"Sign In\" class=\"btn btn-primary btn-lg btn-block\"]\r\n\r\n\r\n<div class=\"form-group\"><br/>\r\n[link-registration class=\"reg\" label=\"Register\"] | [link-lost-password class=\"lostp\" label=\"Forgot Password?\"]\r\n</div>\r\n</div>\r\n','/* css class for the login generated errors */\r\n\r\n.profilepress-login-status {\r\n\r\n    background-color: #34495e;\r\n    color: #ffffff;\r\n    border: medium none;\r\n    border-radius: 4px;\r\n    font-size: 15px;\r\n    font-weight: normal;\r\n    line-height: 1.4;\r\n    padding: 8px 5px;\r\n    margin:4px 0;\r\n    transition: border 0.25s linear 0s, color 0.25s linear 0s, background-color 0.25s linear 0s;\r\n}\r\n\r\n.profilepress-login-status a {\r\n  color: #ea9629 !important;\r\n}\r\n\r\n/*\r\nThis login form uses the FlatUI css stylesheet that ships with the plugin hence this look.\r\nIt\'s actually very pretty when implemented.\r\n*/','2017-09-22'),(2,'Fzbuk Login Theme','<div class=\"fzbuk-login-form-wrap\">\r\n\r\n	<h1>Sign In</h1>\r\n\r\n	<div class=\"fzbuk-login-form\">\r\n\r\n		<label>\r\n			[login-username placeholder=\"Username\" id=\"fzbuk-username\"]\r\n		</label>\r\n\r\n		<label>\r\n			[login-password placeholder=\"Password\" id=\"fzbuk-password\"]\r\n		</label>\r\n\r\n		[login-submit value=\"Login\"]\r\n\r\n	</div>\r\n	<h5>[link-lost-password class=\"lostp\" label=\"Forgot Password?\"]</h5>\r\n</div>\r\n','@import url(http://fonts.googleapis.com/css?family=Lato:400,700);\r\n\r\n/* css class for the login generated errors */\r\n.profilepress-login-status {\r\n  -webkit-box-sizing: border-box;\r\n  -moz-box-sizing: border-box;\r\n  box-sizing: border-box;\r\n  color: #fff;\r\n  background: #5170ad;\r\n  background: -moz-radial-gradient(center, ellipse cover, #5170ad 0%, #355493 100%);\r\n  background: -webkit-gradient(radial, center center, 0px, center center, 100%, color-stop(0%, #5170ad), color-stop(100%, #355493));\r\n  background: -webkit-radial-gradient(center, ellipse cover, #5170ad 0%, #355493 100%);\r\n  background: -o-radial-gradient(center, ellipse cover, #5170ad 0%, #355493 100%);\r\n  background: -ms-radial-gradient(center, ellipse cover, #5170ad 0%, #355493 100%);\r\n  background: radial-gradient(ellipse at center, #5170ad 0%, #355493 100%);\r\n  filter: progid:DXImageTransform.Microsoft.gradient( startColorstr=\'#5170ad\', endColorstr=\'#355493\',GradientType=1 );\r\n  border: 1px solid #2d416d;\r\n  box-shadow: 0 1px #5670A4 inset, 0 0 10px 5px rgba(0, 0, 0, 0.1);\r\n  border-radius: 5px;\r\n  position: relative;\r\n  width: 360px;\r\n  text-align: center;\r\n  margin: 10px auto;\r\n  padding: 10px;\r\n}\r\n\r\n.profilepress-login-status a {\r\ncolor: #ea9629 !important;\r\nfont-weight:bold;\r\n}\r\n\r\n\r\n\r\n.fzbuk-login-form label {\r\n  display: block;\r\n  margin: 0 !important;\r\n}\r\n\r\n.fzbuk-login-form-wrap {\r\n  -webkit-box-sizing: border-box;\r\n  -moz-box-sizing: border-box;\r\n  box-sizing: border-box;\r\n  background: #5170ad;\r\n  background: -moz-radial-gradient(center, ellipse cover, #5170ad 0%, #355493 100%);\r\n  background: -webkit-gradient(radial, center center, 0px, center center, 100%, color-stop(0%, #5170ad), color-stop(100%, #355493));\r\n  background: -webkit-radial-gradient(center,